class Solution:
    def longestValidParentheses(self, s: str) -> int:
        s_len = len(s)
        dp = [0 for _ in s]
        ans = 0

        for i in range(1, s_len):
            if s[i] == ')':
                if s[i-1] == '(':
                    dp[i] = 2
                    if i >= 2:
                        dp[i] += dp[i-2]
                elif dp[i-1] > 0:
                    if (i - 1 - dp[i-1] >= 0) and s[i - 1 - dp[i-1]] == '(':
                        dp[i] = dp[i-1] + 2
                        if (i - dp[i] >= 0) and dp[i - dp[i]] > 0:
                            dp[i] += dp[i - dp[i]]
            ans = max(ans, dp[i])

        return ans
            

